import { useEffect, useState } from 'react';
import './App.css';

const images =[
  {id:'1',imageName: 'images1.jpg' , tag: 'free'},
  {id:'2',imageName: 'images2.jpg' , tag: 'free'},
  {id:'3',imageName: 'images3.jpg' , tag: 'pro'},
  {id:'4',imageName: 'images4.jpg' , tag: 'free'},
  {id:'5',imageName: 'images5.jpg' , tag: 'free'},
  {id:'6',imageName: 'images6.jpg' , tag: 'pro'},
  {id:'7',imageName: 'images7.jpg' , tag: 'free'},
  {id:'8',imageName: 'images8.jpg' , tag: 'pro'},
  {id:'91',imageName: 'images9.jpg' , tag: 'free'},
  {id:'10',imageName: 'images10.jpg' , tag: 'pro'},
  {id:'11',imageName: 'images11.jpg' , tag: 'free'},
];

function App() {
  const [tag , setTag]= useState('all');
  const[filteredImages , setFilteredImages]= useState([]);

  useEffect(()=>{
    tag == 'all' ? setFilteredImages(images) : setFilteredImages(images.filter(image => image.tag == tag))

  },[tag])
  return (
    <div className="App" handleSetTag>
      <div>
        <TagButton name="all"/>
        <TagButton name="free"/>
        <TagButton name="pro"/>
        {filteredImages.map( image => <div>{image.imageName}</div>)}
      </div>



    </div>
  );
}

const TagButton =({name}) => {
  return <button>{name.toUpperCase()}</button>
}

export default App;
